configuration.sqlCommand:
revoke all on ${database.name}.* from
'${database.user}'@'${database.server}';
revoke all on ${database.name}.* from
'${database.user}'@'%';
drop database if exists ${database.name};
create database
${database.name} default character set latin1;
grant all on ${database.name}.* TO
'${database.user}'@'${database.server}' identified by '${database.password}';
grant all on ${database.name}.* TO '${database.user}'@'%' identified by
'${database.password}';
